tables.table_schema:
CREATE TABLE smart_devices (
    device_id UUID PRIMARY KEY,
    device_name VARCHAR(50) NOT NULL,
    device_type VARCHAR(50) NOT NULL,
    power_consumption DECIMAL
);

CREATE TABLE routines (
    routine_id UUID PRIMARY KEY,
    routine_name VARCHAR(50) NOT NULL,
    start_time TIME,
    end_time TIME
);

CREATE TABLE smart_home_devices (
    device_id INT PRIMARY KEY,
    device_name TEXT NOT NULL,
    device_type TEXT NOT NULL,
    device_location TEXT
);

CREATE TABLE energy_usage (
    timestamp TIMESTAMP PRIMARY KEY,
    device_id INT NOT NULL,
    energy_consumed FLOAT NOT NULL
);

CREATE TABLE rooms (
   room_id INT PRIMARY KEY,
   room_name VARCHAR(50),
   floor_number INT,
   is_occupied BOOLEAN
);

CREATE TABLE routines (
   routine_id INT PRIMARY KEY,
   routine_name VARCHAR(50),
   description TEXT,
   is_enabled BOOLEAN
);

CREATE TABLE energy_consumption (
   energy_id SERIAL PRIMARY KEY,
   device_id INT,
   timestamp TIMESTAMP,
   energy_used DECIMAL
);

CREATE TABLE rooms (
    room_id INT PRIMARY KEY,
    room_name VARCHAR(50) NOT NULL,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL
);

CREATE TABLE energy_logs (
    log_id INT PRIMARY KEY,
    device_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    energy_consumed FLOAT
);
