);

CREATE TABLE images (
    id UUID PRIMARY KEY,
    name VARCHAR(255),
    file_path VARCHAR(255),
    size INT
);

CREATE TABLE videos (
    id UUID PRIMARY KEY,
    name VARCHAR(255),
    file_path VARCHAR(255),
    duration INT
);

CREATE TABLE tags (
    id UUID PRIMARY KEY,
    name VARCHAR(50)
);

CREATE TABLE ar_assets ( 
  id INT PRIMARY KEY, 
  asset_name VARCHAR(255), 
  asset_type VARCHAR(50), 
  upload_date TIMESTAMP 
);

CREATE TABLE ar_images ( 
  id INT PRIMARY KEY, 
  image_url VARCHAR(255), 
  image_size INT, 
  asset_id INT REFERENCES ar_assets(id) 
);

CREATE TABLE ar_videos ( 
  id INT PRIMARY KEY, 
  video_url VARCHAR(255), 
  video_duration INT, 
  asset_id INT REFERENCES ar_assets(id) 
);

CREATE TABLE ar_tags ( 
  tag_id INT PRIMARY KEY, 
  tag_name VARCHAR(50) 
);

CREATE TABLE asset_tags ( 
  asset_id INT REFERENCES ar_assets(id), 
  tag_id INT REFERENCES ar_tags(tag_id) 
);
