tables.table_schema:
CREATE TABLE hyperparameters (
	hyperparameter_id INT PRIMARY KEY,
	hyperparameter_name VARCHAR(50) NOT NULL,
	hyperparameter_value VARCHAR(255) NOT NULL
);

CREATE TABLE training_configurations (
	config_id INT PRIMARY KEY,
	config_name VARCHAR(50) NOT NULL,
	config_value VARCHAR(255) NOT NULL,
	config_description TEXT
);

CREATE TABLE Hyperparameters (
	parameter_id INT PRIMARY KEY,
	parameter_name VARCHAR(255),
	parameter_value VARCHAR(255)
);

CREATE TABLE TrainingConfigurations (
	config_id INT PRIMARY KEY,
	config_name VARCHAR(255),
	config_value VARCHAR(255),
	config_description TEXT
);

CREATE TABLE ExperimentResults (
	result_id INT PRIMARY KEY,
	experiment_id INT,
	result_metrics JSON
);

CREATE TABLE ReproducibilityLogs (
	log_id INT PRIMARY KEY,
	timestamp TIMESTAMP,
	action VARCHAR(255),
	details TEXT
);
