tables.table_schema:
CREATE TABLE designs(
    design_id SERIAL PRIMARY KEY,
    design_name VARCHAR(255) NOT NULL,
    designer_name VARCHAR(255) NOT NULL,
    uploaded_date TIMESTAMP
);

CREATE TABLE design_tags (
	tag_id INT PRIMARY KEY,
	tag_name VARCHAR(50),
	tag_description TEXT
);

CREATE TABLE design_catalog (
    design_id INT PRIMARY KEY,
    design_name VARCHAR(255),
    designer_name VARCHAR(255),
    upload_date DATE
);

CREATE TABLE design_tags (
    tag_id INT PRIMARY KEY,
    tag_name VARCHAR(50)
);

CREATE TABLE design_printers (
    printer_id INT PRIMARY KEY,
    printer_name VARCHAR(100),
    printer_type VARCHAR(50),
    printer_build_volume VARCHAR(50)
);

CREATE TABLE design_licenses (
    license_id INT PRIMARY KEY,
    license_name VARCHAR(100),
    license_type VARCHAR(50),
    allows_commercial_use BOOLEAN
);

CREATE TABLE tags (
    tag_id SERIAL PRIMARY KEY,
    tag_name TEXT NOT NULL UNIQUE
);
