tables.table_schema:
CREATE TABLE RobotMetrics (
    robot_id UUID PRIMARY KEY,
    performance_metric_name VARCHAR(255),
    metric_value DECIMAL,
    capture_timestamp TIMESTAMP
);

CREATE TABLE RobotMetrics (
    robot_id INT,
    performance_metric_name VARCHAR(255),
    metric_value FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE RobotConfigurations (
    robot_id INT,
    configuration_name VARCHAR(255),
    configuration_value VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE RobotErrors (
    error_id INT,
    robot_id INT,
    error_message TEXT,
    error_timestamp TIMESTAMP
);

CREATE TABLE robots (
    robot_id INT PRIMARY KEY,
    robot_name VARCHAR(50) NOT NULL,
    manufacturing_date DATE,
    performance_score FLOAT
);

CREATE TABLE robots_performance_metrics (
    robot_id INT PRIMARY KEY,
    performance_metric_name VARCHAR(50) NOT NULL,
    performance_metric_value FLOAT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE robot_errors (
    error_id INT PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    error_message TEXT NOT NULL,
    robot_id INT NOT NULL
);

CREATE TABLE robot_details (
    robot_id INT PRIMARY KEY,
    robot_name VARCHAR(50) NOT NULL,
    manufacturing_date DATE NOT NULL,
    battery_capacity FLOAT NOT NULL
);
