tables.table_schema:
CREATE TABLE robot_maintenance_schedule (
    schedule_id INT PRIMARY KEY,
    robot_id INT,
    maintenance_date DATE,
    maintenance_type VARCHAR(50)
);

CREATE TABLE Robot_Maintenance_Schedule (
    robot_id INT,
    maintenance_date DATE,
    maintenance_type VARCHAR(50),
    technician_id INT
);

CREATE TABLE Technicians (
    technician_id INT,
    technician_name VARCHAR(50),
    technician_specialty VARCHAR(50),
    technician_experience_years INT
);

CREATE TABLE robot_maintenance_schedule (
  robot_id INT PRIMARY KEY,
  maintenance_date DATE,
  maintenance_type VARCHAR(50),
  maintenance_description TEXT
);

CREATE TABLE robot_status (
  robot_id INT PRIMARY KEY,
  status VARCHAR(50),
  last_update TIMESTAMP
);

CREATE TABLE robot_inventory (
  robot_id SERIAL PRIMARY KEY,
  robot_name VARCHAR(50),
  build_date DATE,
  location VARCHAR(50)
);

CREATE TABLE robot_owners (
   owner_id INT PRIMARY KEY,
   owner_name VARCHAR(50) NOT NULL
);

CREATE TABLE robot_models (
   model_id INT PRIMARY KEY,
   model_name VARCHAR(50) NOT NULL,
   manufacturing_date DATE
);

CREATE TABLE maintenance_records (
   record_id INT PRIMARY KEY,
   robot_id INT NOT NULL,
   maintenance_date DATE,
   maintenance_description TEXT
);

CREATE TABLE service_centers (
   center_id INT PRIMARY KEY,
   center_name VARCHAR(50) NOT NULL,
   location VARCHAR(100) NOT NULL
);

CREATE TABLE service_schedule (
   schedule_id INT PRIMARY KEY,
   robot_id INT NOT NULL,
   scheduled_date DATE,
   status VARCHAR(20) NOT NULL
);
