CREATE TABLE Robot_Calibration_Data (
    id INT PRIMARY KEY,
    robot_id INT,
    calibration_date DATE,
    calibration_values JSON
);

CREATE TABLE Robot_Adjustments (
    id INT PRIMARY KEY,
    robot_id INT,
    adjustment_date DATE,
    adjustment_details TEXT
);

CREATE TABLE robot_calibration_data (
    id INT PRIMARY KEY,
    robot_id INT,
    calibration_date DATE,
    calibration_data JSONB
);

CREATE TABLE robot_adjustments (
    id INT PRIMARY KEY,
    robot_id INT,
    adjustment_date DATE,
    adjustment_details TEXT
);

CREATE TABLE robot_list (
    id INT PRIMARY KEY,
    robot_name VARCHAR(50),
    manufacturer VARCHAR(50),
    year_acquired INT
);

CREATE TABLE RobotCalibrationData (
    robot_id INT PRIMARY KEY,
    calibration_date DATE,
    calibration_time TIME,
    calibration_value FLOAT
);

CREATE TABLE robot (
  id SERIAL PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  model VARCHAR(50),
  manufacturer VARCHAR(50)
);

CREATE TABLE technicians (
  id SERIAL PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  specialization VARCHAR(50),
  joined_date DATE
);
