tables.table_schema:
CREATE TABLE error_logs (
    id INT PRIMARY KEY,
    robot_id INT,
    log_message TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE error_logs (
  error_id INT NOT NULL,
  error_description TEXT NOT NULL,
  error_timestamp TIMESTAMP NOT NULL,
  robot_id INT NOT NULL
);

CREATE TABLE troubleshooting_records (
  record_id INT NOT NULL,
  record_details TEXT NOT NULL,
  record_date DATE NOT NULL,
  error_id INT NOT NULL
);

CREATE TABLE robots (
  robot_id INT NOT NULL,
  robot_name VARCHAR(50) NOT NULL,
  robot_type VARCHAR(50) NOT NULL,
  robot_status VARCHAR(20) NOT NULL
);

CREATE TABLE robot_systems (
  system_id INT NOT NULL,
  system_name VARCHAR(50) NOT NULL,
  system_description TEXT NOT NULL,
  robot_id INT NOT NULL
);

CREATE TABLE robot_status (
	id INT PRIMARY KEY,
	robot_id INT,
	error_count INT,
	system_status TEXT
);

CREATE TABLE robot_logs (
	id INT PRIMARY KEY,
	log_type TEXT,
	log_message TEXT,
	timestamp TIMESTAMP
);

CREATE TABLE robot_system_info (
	id INT PRIMARY KEY,
	robot_id INT,
	hardware_info TEXT,
	software_info TEXT
);
