tables.table_schema:
CREATE TABLE robots_energy_efficiency (
  robot_id INT,
  energy_consumption FLOAT,
  efficiency_rating FLOAT
);

CREATE TABLE robots ( 
    robot_id INT PRIMARY KEY, 
    robot_name VARCHAR(50), 
    energy_consumption DECIMAL(10, 2), 
    efficiency DECIMAL(5, 2) 
);

CREATE TABLE energy_consumption (
    robot_id INT PRIMARY KEY,
    energy_consumed FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE robot_efficiency (
    robot_id INT PRIMARY KEY,
    efficiency_rating FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE WorkLogs (
    id SERIAL PRIMARY KEY,
    robot_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    action VARCHAR(100) NOT NULL
);

CREATE TABLE MaintenanceLogs (
    id SERIAL PRIMARY KEY,
    robot_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    issue VARCHAR(100) NOT NULL
);

CREATE TABLE EfficiencyHistory (
    id SERIAL PRIMARY KEY,
    robot_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    efficiency DECIMAL(10,2) NOT NULL
);
