tables.table_schema:
CREATE TABLE UserPermissions ( 
    user_id INT NOT NULL, 
    permission_type VARCHAR(50) NOT NULL, 
    access_level INT NOT NULL, 
    PRIMARY KEY (user_id, permission_type) 
);

CREATE TABLE AccessControlLists ( 
    list_id INT NOT NULL, 
    resource_name VARCHAR(50) NOT NULL, 
    allowed_users JSONB NOT NULL, 
    PRIMARY KEY (list_id) 
);

CREATE TABLE user_permissions (
    user_id INT PRIMARY KEY,
    permission_type VARCHAR(50)
);

CREATE TABLE access_control_lists (
    acl_id INT PRIMARY KEY,
    user_id INT,
    resource_id INT,
    access_type VARCHAR(50)
);

CREATE TABLE blockchain_platform (
    block_id INT PRIMARY KEY,
    block_data TEXT,
    user_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE user_permissions (
    user_id INT PRIMARY KEY,
    permission_type VARCHAR(50) NOT NULL,
    granted_at TIMESTAMP
);

CREATE TABLE access_control_list (
    user_id INT,
    resource_id INT,
    access_level VARCHAR(50),
    PRIMARY KEY (user_id, resource_id)
);

CREATE TABLE user_roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL
);

CREATE TABLE role_permissions (
    role_id INT,
    permission_type VARCHAR(50) NOT NULL,
    PRIMARY KEY (role_id, permission_type)
);

CREATE TABLE user_accounts (
user_id INT PRIMARY KEY,
username VARCHAR(50) NOT NULL,
password VARCHAR(255) NOT NULL,
email VARCHAR(100) NOT NULL
);

CREATE TABLE roles (
role_id INT PRIMARY KEY,
role_name VARCHAR(50) NOT NULL
);

CREATE TABLE permissions (
permission_id INT PRIMARY KEY,
permission_name VARCHAR(50) NOT NULL
);
