tables.table_schema:
CREATE TABLE digital_asset_tokens (
    token_id SERIAL PRIMARY KEY,
    token_name VARCHAR(255) NOT NULL,
    token_owner VARCHAR(255) NOT NULL,
    token_value DECIMAL(18,2) NOT NULL
);

CREATE TABLE digital_assets (
  asset_id INT PRIMARY KEY,
  asset_name VARCHAR(255) NOT NULL,
  asset_symbol VARCHAR(10) NOT NULL,
  asset_value DECIMAL(18, 2) NOT NULL
);

CREATE TABLE asset_owners (
  owner_id INT PRIMARY KEY,
  owner_name VARCHAR(255) NOT NULL,
  email VARCHAR(255) UNIQUE NOT NULL,
  created_at TIMESTAMP NOT NULL
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE asset_tokens (
    id INT PRIMARY KEY,
    token_name VARCHAR(50) NOT NULL,
    token_symbol CHAR(3) NOT NULL,
    total_supply BIGINT NOT NULL
);

CREATE TABLE token_owners (
    owner_id INT PRIMARY KEY,
    token_id INT,
    owner_address VARCHAR(50) NOT NULL,
    ownership_percentage DECIMAL(5,2) NOT NULL
);

CREATE TABLE token_metadata (
    token_id INT PRIMARY KEY,
    metadata_key VARCHAR(50) NOT NULL,
    metadata_value VARCHAR(255) NOT NULL
);

CREATE TABLE token_contracts (
    contract_id INT PRIMARY KEY,
    token_id INT,
    contract_address VARCHAR(50) NOT NULL,
    contract_type VARCHAR(50) NOT NULL
);
