CREATE TABLE user_data (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    encrypted_data VARCHAR(200) NOT NULL
);

CREATE TABLE user_keys (
    user_id INT PRIMARY KEY,
    encryption_key VARCHAR(100) NOT NULL,
    decryption_key VARCHAR(100) NOT NULL,
    public_key VARCHAR(150) NOT NULL
);

CREATE TABLE blockchain (
    block_id INT PRIMARY KEY,
    block_data TEXT NOT NULL,
    block_hash VARCHAR(100) NOT NULL
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  email VARCHAR(100) NOT NULL,
  password_hash VARCHAR(100) NOT NULL
);

CREATE TABLE user_data (
  data_id INT PRIMARY KEY,
  user_id INT REFERENCES users(user_id),
  encrypted_data TEXT NOT NULL
);

CREATE TABLE blockchain (
  block_id INT PRIMARY KEY,
  block_hash VARCHAR(100) NOT NULL,
  previous_block_hash VARCHAR(100),
  timestamp TIMESTAMP
);

CREATE TABLE transactions (
  transaction_id INT PRIMARY KEY,
  block_id INT REFERENCES blockchain(block_id),
  data_id INT REFERENCES user_data(data_id),
  timestamp TIMESTAMP
);

CREATE TABLE logs (
  log_id INT PRIMARY KEY,
  user_id INT REFERENCES users(user_id),
  action VARCHAR(50) NOT NULL,
  timestamp TIMESTAMP
);
