tables.table_schema:
CREATE TABLE blockchain_services (
    service_id INT PRIMARY KEY,
    service_name VARCHAR(50),
    description TEXT,
    user_id INT REFERENCES user_profiles(user_id)
);

CREATE TABLE blockchain_services (
    service_id SERIAL PRIMARY KEY,
    service_name VARCHAR(50) NOT NULL,
    description TEXT,
    base_price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE user_service_mapping (
    mapping_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES user_profiles(user_id),
    service_id INT REFERENCES blockchain_services(service_id),
    subscription_date TIMESTAMP NOT NULL
);

CREATE TABLE UserProfiles(
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    profile_pic_url VARCHAR(255)
);

CREATE TABLE UserPreferences(
    preference_id INT PRIMARY KEY,
    user_id INT,
    preferred_coin VARCHAR(10),
    notification_enabled BOOLEAN
);

CREATE TABLE CoinPrices(
    coin_id INT PRIMARY KEY,
    coin_name VARCHAR(50),
    current_price DECIMAL(10, 2),
    last_updated TIMESTAMP
);

CREATE TABLE UserActivity(
    activity_id INT PRIMARY KEY,
    user_id INT,
    activity_type VARCHAR(20),
    activity_timestamp TIMESTAMP
);
