tables.table_schema:
CREATE TABLE analytics (
    id INT PRIMARY KEY,
    block_id INT,
    analysis_result TEXT,
    analysis_date DATE
);

CREATE TABLE blockchain_data (
    block_id INT PRIMARY KEY,
    block_timestamp TIMESTAMP,
    transaction_count INT,
    hash VARCHAR(255)
);

CREATE TABLE performance_metrics (
    metric_id INT PRIMARY KEY,
    block_id INT,
    cpu_usage DECIMAL(10, 2),
    memory_usage DECIMAL(10, 2)
);

CREATE TABLE analytics_insights (
    insight_id INT PRIMARY KEY,
    metric_id INT,
    analysis_date DATE,
    recommendation TEXT
);

CREATE TABLE blockchain_data (
    block_number INT,
    timestamp TIMESTAMP,
    transactions INT,
    gas_used DECIMAL
);

CREATE TABLE block_size (
    block_number INT,
    block_size_MB DECIMAL,
    transaction_count INT
);

CREATE TABLE miner_performance (
    miner_id INT,
    mining_power DECIMAL,
    block_reward DECIMAL
);

CREATE TABLE network_activity (
    timestamp TIMESTAMP,
    active_nodes INT,
    average_latency DECIMAL
);

CREATE TABLE performance_metrics (
    timestamp TIMESTAMP,
    throughput_Mbps DECIMAL,
    latency_ms DECIMAL
);
