tables.table_schema:
CREATE TABLE consensus_voting_mechanisms (
    id INT PRIMARY KEY,
    algorithm VARCHAR(50),
    votes INT
);

CREATE TABLE Consensus_Algorithms (
    id INT PRIMARY KEY,
    algorithm_name TEXT NOT NULL,
    description TEXT
);

CREATE TABLE Voting_Mechanisms (
    id INT PRIMARY KEY,
    mechanism_name TEXT NOT NULL,
    purpose TEXT
);

CREATE TABLE consensus_algorithms (
    id INT,
    algorithm_name VARCHAR(255),
    description TEXT,
    PRIMARY KEY (id)
);

CREATE TABLE voting_mechanisms (
    id INT,
    mechanism_name VARCHAR(255),
    voting_type VARCHAR(50),
    PRIMARY KEY (id)
);

CREATE TABLE consensus_algorithms (
    id SERIAL PRIMARY KEY,
    algorithm_name TEXT NOT NULL,
    description TEXT,
    version FLOAT
);

CREATE TABLE voting_mechanisms (
    id SERIAL PRIMARY KEY,
    mechanism_name TEXT NOT NULL,
    description TEXT,
    quorum INTEGER
);

CREATE TABLE blockchain_networks (
    id SERIAL PRIMARY KEY,
    network_name TEXT NOT NULL,
    genesis_block_hash TEXT,
    consensus_algorithm_id INT REFERENCES consensus_algorithms(id)
);

CREATE TABLE block_transactions (
    id SERIAL PRIMARY KEY,
    block_id INTEGER REFERENCES blockchain_networks(id),
    transaction_id INTEGER,
    timestamp TIMESTAMPTZ NOT NULL
);

CREATE TABLE consensus_algorithm (
    algorithm_id INT PRIMARY KEY,
    algorithm_name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE voting_member (
    member_id INT PRIMARY KEY,
    member_name VARCHAR(50) NOT NULL,
    member_address VARCHAR(100),
    is_active BOOLEAN
);

CREATE TABLE block (
    block_id INT PRIMARY KEY,
    block_hash VARCHAR(100) UNIQUE NOT NULL,
    previous_block_hash VARCHAR(100),
    algorithm_id INT
);

CREATE TABLE blockchain (
    chain_id INT PRIMARY KEY,
    chain_name VARCHAR(50) NOT NULL,
    current_block_id INT
);
