CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    password_hash VARCHAR(100),
    email VARCHAR(100) UNIQUE
);

CREATE TABLE roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50) UNIQUE
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    password_hash BYTEA NOT NULL,
    salt BYTEA NOT NULL
);

CREATE TABLE user_roles (
    role_id SERIAL PRIMARY KEY,
    role_name VARCHAR(50) UNIQUE NOT NULL
);

CREATE TABLE user_permissions (
    permission_id SERIAL PRIMARY KEY,
    permission_name VARCHAR(50) UNIQUE NOT NULL
);

CREATE TABLE user_role_permissions (
    role_id INT REFERENCES user_roles(role_id),
    permission_id INT REFERENCES user_permissions(permission_id),
    PRIMARY KEY(role_id, permission_id)
);

CREATE TABLE user_credentials (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password_hash VARCHAR(100) NOT NULL,
    salt VARCHAR(50) NOT NULL
);

CREATE TABLE user_roles (
    role_id SERIAL PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL
);

CREATE TABLE user_roles_mapping (
    user_id INTEGER REFERENCES user_credentials(user_id),
    role_id INTEGER REFERENCES user_roles(role_id)
);

CREATE TABLE user_sessions (
    session_id UUID PRIMARY KEY,
    user_id INTEGER REFERENCES user_credentials(user_id),
    login_time TIMESTAMP
);

CREATE TABLE user_activity_logs (
    log_id SERIAL PRIMARY KEY,
    user_id INTEGER REFERENCES user_credentials(user_id),
    activity_description TEXT,
    activity_time TIMESTAMP
);
