tables.table_schema:
CREATE TABLE sensitive_information (
	id INT PRIMARY KEY,
	name VARCHAR(50),
	sensitive_data VARCHAR(100)
);

CREATE TABLE encryption_keys (
	key_id INT PRIMARY KEY,
	key_name VARCHAR(50),
	key_value VARCHAR(100)
);

CREATE TABLE sensitive_data (
    id UUID PRIMARY KEY,
    name STRING,
    email STRING,
    encrypted_info BYTES
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username STRING,
    password BYTES,
    role STRING
);
