tables.table_schema:
CREATE TABLE vulnerability_management (
  vulnerability_id INT PRIMARY KEY,
  vulnerability_name VARCHAR(255) NOT NULL,
  priority_level INT,
  remediation_status VARCHAR(50)
);

CREATE TABLE vulnerabilities (
    vulnerability_id INT PRIMARY KEY,
    vulnerability_name VARCHAR(255),
    severity_level VARCHAR(50),
    remediation_step TEXT
);

CREATE TABLE vulnerability_tracking (
    tracking_id INT PRIMARY KEY,
    vulnerability_id INT REFERENCES vulnerabilities(vulnerability_id),
    tracking_status VARCHAR(50),
    last_updated_at TIMESTAMP
);

CREATE TABLE vulnerability_scans (
    scan_id INT PRIMARY KEY,
    scan_date DATE,
    scanner_name VARCHAR(100),
    scan_results JSON
);

CREATE TABLE vulnerability (
    vulnerability_id INT PRIMARY KEY,
    name VARCHAR(255),
    description TEXT,
    severity INT
);

CREATE TABLE asset (
    asset_id INT PRIMARY KEY,
    name VARCHAR(255),
    ip_address VARCHAR(15),
    owner VARCHAR(255)
);

CREATE TABLE vulnerability_status (
    status_id INT PRIMARY KEY,
    status_name VARCHAR(50),
    vulnerability_id INT,
    asset_id INT
);
