tables.table_schema:
CREATE TABLE forensic_analysis (
    case_id UUID PRIMARY KEY,
    suspect_name VARCHAR(255),
    incident_date TIMESTAMP,
    incident_details TEXT
);

CREATE TABLE security_incidents (
    incident_id INT PRIMARY KEY,
    incident_description TEXT,
    incident_date TIMESTAMP,
    severity INT
);
CREATE TABLE evidence (
    evidence_id INT PRIMARY KEY,
    incident_id INT REFERENCES security_incidents(incident_id),
    evidence_info TEXT,
    evidence_date TIMESTAMP
);

CREATE TABLE incident_reports (
    id SERIAL PRIMARY KEY,
    report_name VARCHAR(255),
    incident_date DATE,
    description TEXT
);

CREATE TABLE IncidentReports (
    report_id INT PRIMARY KEY,
    incident_date DATE,
    description TEXT,
    investigator_id INT
);

CREATE TABLE Investigators (
    investigator_id INT PRIMARY KEY,
    name VARCHAR(50),
    specialization TEXT
);

CREATE TABLE Evidence (
    evidence_id INT PRIMARY KEY,
    report_id INT,
    type VARCHAR(50),
    description TEXT
);

CREATE TABLE Suspects (
    suspect_id INT PRIMARY KEY,
    name VARCHAR(50),
    description TEXT,
    status VARCHAR(20)
);

CREATE TABLE Investigator
(
    investigator_id INT PRIMARY KEY,
    investigator_name VARCHAR(50),
    investigator_role VARCHAR(50)
);
