tables.table_schema:
CREATE TABLE threats (
  threat_id SERIAL PRIMARY KEY,
  threat_name VARCHAR(100) NOT NULL,
  threat_description TEXT,
  threat_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE ThreatActors (
    actor_id INT PRIMARY KEY,
    actor_name STRING,
    actor_type STRING,
    known_associations STRING
);

CREATE TABLE Indicators (
    indicator_id INT PRIMARY KEY,
    indicator_type STRING,
    indicator_value STRING,
    creation_date DATE
);

CREATE TABLE Incidents (
    incident_id INT PRIMARY KEY,
    incident_type STRING,
    incident_description STRING,
    incident_status STRING
);

CREATE TABLE Mitigations (
    mitigation_id INT PRIMARY KEY,
    mitigation_type STRING,
    mitigation_description STRING,
    implementation_date DATE
);
