tables.table_schema:
CREATE TABLE DataCleansingTasks (
    task_id INT PRIMARY KEY,
    task_name VARCHAR(255),
    task_description TEXT,
    task_status VARCHAR(50)
);

CREATE TABLE DatasetInformation (
    dataset_id INT PRIMARY KEY,
    dataset_name VARCHAR(255),
    dataset_description TEXT,
    dataset_size INT
);

CREATE TABLE SQLQueries (
    query_id INT PRIMARY KEY,
    query_text TEXT,
    query_author VARCHAR(100),
    query_execution_time FLOAT
);

CREATE TABLE TransformationTasks (
    transformation_id INT PRIMARY KEY,
    transformation_name VARCHAR(255),
    transformation_description TEXT,
    transformation_status VARCHAR(50)
);

CREATE TABLE data_cleansing_tasks (
    task_id UUID PRIMARY KEY,
    task_name VARCHAR(50) NOT NULL,
    task_description TEXT,
    task_status VARCHAR(20) DEFAULT 'Pending'
);

CREATE TABLE data_transformation_tasks (
    trans_id UUID PRIMARY KEY,
    trans_name VARCHAR(50) NOT NULL,
    trans_query TEXT,
    trans_status VARCHAR(20) DEFAULT 'In Progress'
);

CREATE TABLE massive_datasets (
    dataset_id UUID PRIMARY KEY,
    dataset_name VARCHAR(50) NOT NULL,
    dataset_size BIGINT,
    dataset_format VARCHAR(20) DEFAULT 'CSV'
);

CREATE TABLE task_logs (
    log_id UUID PRIMARY KEY,
    task_id UUID REFERENCES data_cleansing_tasks(task_id),
    log_message TEXT,
    log_timestamp TIMESTAMP
);

CREATE TABLE transformation_logs (
    tr_log_id UUID PRIMARY KEY,
    trans_id UUID REFERENCES data_transformation_tasks(trans_id),
    tr_log_message TEXT,
    tr_log_timestamp TIMESTAMP
);
