tables.table_schema:
CREATE TABLE Real_World_Object (
    id SERIAL PRIMARY KEY,
    object_name VARCHAR(50) NOT NULL,
    object_description TEXT,
    object_location POINT
);

CREATE TABLE ar_overlay_info (
    overlay_id UUID PRIMARY KEY,
    object_id UUID NOT NULL,
    overlay_name VARCHAR(255) NOT NULL,
    overlay_description TEXT
);

CREATE TABLE object_data (
    object_id UUID PRIMARY KEY,
    object_name VARCHAR(255) NOT NULL,
    object_type VARCHAR(50) NOT NULL,
    creation_date TIMESTAMP
);

CREATE TABLE ar_applications (
    app_id UUID PRIMARY KEY,
    app_name VARCHAR(255) NOT NULL,
    app_version VARCHAR(20) NOT NULL,
    app_description TEXT
);

CREATE TABLE ar_overlay_info (
    overlay_id SERIAL PRIMARY KEY,
    overlay_name VARCHAR(255) NOT NULL,
    overlay_type VARCHAR(50),
    overlay_description TEXT,
    object_id INT REFERENCES real_world_objects(object_id)
);

CREATE TABLE real_world_objects (
    object_id SERIAL PRIMARY KEY,
    object_name VARCHAR(255) NOT NULL,
    object_category VARCHAR(50),
    object_description TEXT,
    object_location POINT
);

CREATE TABLE ar_mappings (
    mapping_id SERIAL PRIMARY KEY,
    overlay_id INT REFERENCES ar_overlay_info(overlay_id),
    object_id INT REFERENCES real_world_objects(object_id),
    map_description TEXT,
    map_image BYTEA
);

CREATE TABLE ar_settings (
    setting_id SERIAL PRIMARY KEY,
    setting_name VARCHAR(100) NOT NULL,
    setting_value TEXT
);

CREATE TABLE ar_users (
    user_id SERIAL PRIMARY KEY,
    user_name VARCHAR(100) NOT NULL,
    user_email VARCHAR(255) UNIQUE
);
