tables.table_schema:
CREATE TABLE Patients (
  patient_id INT PRIMARY KEY,
  patient_name VARCHAR(50),
  patient_age INT,
  patient_gender VARCHAR(10)
);

CREATE TABLE GeneticData (
  genetic_id INT PRIMARY KEY,
  patient_id INT REFERENCES PatientRecords(patient_id),
  genetic_sequence TEXT,
  analysis_result TEXT
);

CREATE TABLE patient_records (
    patient_id INT PRIMARY KEY,
    patient_name TEXT,
    patient_age INT,
    patient_gender TEXT
);

CREATE TABLE genetic_data (
    genetic_id INT PRIMARY KEY,
    patient_id INT REFERENCES patient_records(patient_id),
    genetic_sequence TEXT,
    genetic_variation TEXT
);

CREATE TABLE research_studies (
    study_id INT PRIMARY KEY,
    study_name TEXT,
    study_description TEXT
);

CREATE TABLE study_results (
    result_id INT PRIMARY KEY,
    study_id INT REFERENCES research_studies(study_id),
    patient_id INT REFERENCES patient_records(patient_id),
    result_data TEXT
);

CREATE TABLE genetic_data (
    genetic_id INT PRIMARY KEY,
    patient_id INT,
    genetic_sequence TEXT,
    mutation_type VARCHAR(50)
);

CREATE TABLE research_studies (
    study_id INT PRIMARY KEY,
    study_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE patient_studies (
    patient_id INT,
    study_id INT,
    PRIMARY KEY (patient_id, study_id)
);

CREATE TABLE research_results (
    result_id INT PRIMARY KEY,
    study_id INT,
    patient_id INT,
    result_data TEXT
);
