tables.table_schema:
CREATE TABLE cell_types ( 
 cell_id INT PRIMARY KEY, 
 cell_type VARCHAR(255) NOT NULL, 
 gene_expression_level FLOAT NOT NULL 
 );

CREATE TABLE CellTypes (
    cell_type_id UUID PRIMARY KEY,
    cell_type_name TEXT,
    gene_expression_data JSONB
);

CREATE TABLE Diseases (
    disease_id UUID PRIMARY KEY,
    disease_name TEXT,
    associated_cell_type_id UUID REFERENCES CellTypes(cell_type_id)
);

CREATE TABLE CellTypes (
    cell_type_id INT PRIMARY KEY,
    cell_type_name VARCHAR(255),
    cell_type_description TEXT
);

CREATE TABLE DiseaseMechanisms (
    disease_id INT PRIMARY KEY,
    disease_name VARCHAR(255),
    associated_genes TEXT,
    related_cell_types TEXT
);

CREATE TABLE CellType (
  cell_type_id UUID PRIMARY KEY,
  cell_type_name TEXT
);

CREATE TABLE Gene (
  gene_id UUID PRIMARY KEY,
  gene_name TEXT,
  gene_symbol TEXT
);

CREATE TABLE DiseaseMechanism (
  mechanism_id UUID PRIMARY KEY,
  mechanism_description TEXT
);

CREATE TABLE CellTypes (
    cell_type_id INT PRIMARY KEY,
    cell_type_name VARCHAR(50) NOT NULL,
    cell_type_description TEXT
);

CREATE TABLE Genes (
    gene_id INT PRIMARY KEY,
