tables.table_schema:
CREATE TABLE genomics_study (
    sample_id UUID PRIMARY KEY,
    sample_name VARCHAR(50) NOT NULL,
    genomic_data JSONB
);

CREATE TABLE proteomics_study (
    sample_id UUID PRIMARY KEY,
    sample_name VARCHAR(50) NOT NULL,
    proteomic_data JSONB
);

CREATE TABLE genomics_data (
	sample_id INT PRIMARY KEY,
	dna_sequence TEXT,
	mutation_count INT
);

CREATE TABLE proteomics_data (
	sample_id INT PRIMARY KEY,
	protein_id INT,
	protein_sequence TEXT
);

CREATE TABLE metabolomics_data (
	sample_id INT PRIMARY KEY,
	metabolite_id INT,
	metabolite_name TEXT,
	metabolite_concentration FLOAT
);

CREATE TABLE Genomics (
    sample_id INT PRIMARY KEY,
    gene_name VARCHAR(50),
    gene_expression FLOAT
);

CREATE TABLE Proteomics (
    sample_id INT PRIMARY KEY,
    protein_id VARCHAR(50),
    protein_abundance FLOAT
);

CREATE TABLE Metabolomics (
    sample_id INT PRIMARY KEY,
    metabolite_name VARCHAR(50),
    metabolite_concentration FLOAT
);
