tables.table_schema:
CREATE TABLE protein_interactions (
  protein_id INT PRIMARY KEY,
  target_protein_id INT,
  interaction_score FLOAT
);

CREATE TABLE protein_targets (
  target_protein_id INT PRIMARY KEY,
  protein_name VARCHAR(255),
  function VARCHAR(255)
);

CREATE TABLE drug_targets (
  drug_id INT PRIMARY KEY,
  target_protein_id INT,
  target_type VARCHAR(255)
);

CREATE TABLE drugs (
  drug_id INT PRIMARY KEY,
  drug_name VARCHAR(255),
  description TEXT
);
