tables.table_schema:
CREATE TABLE experiment_results (
  experiment_id BIGINT PRIMARY KEY,
  experiment_name VARCHAR(255),
  experiment_date DATE,
  result_data JSON
);

CREATE TABLE SampleDetails (
    sample_id INT,
    sample_name VARCHAR(255),
    sample_type VARCHAR(50),
    created_date TIMESTAMP
);

CREATE TABLE ExperimentLogs (
    log_id INT,
    experiment_id INT,
    log_timestamp TIMESTAMP
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255) NOT NULL,
    researcher_email VARCHAR(255)
);

CREATE TABLE experiments (
    experiment_id INT PRIMARY KEY,
    experiment_name VARCHAR(255),
    experiment_date DATE,
    researcher_id INT
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    researcher_department VARCHAR(255)
);

CREATE TABLE results (
    result_id INT PRIMARY KEY,
    experiment_id INT,
    result_value FLOAT,
    result_timestamp TIMESTAMP
);

CREATE TABLE experiments_metadata (
    experiment_id INT,
    metadata_key VARCHAR(255),
    metadata_value TEXT
);

CREATE TABLE experimental_conditions (
    condition_id INT PRIMARY KEY,
    condition_name VARCHAR(255),
    condition_description TEXT
);
