tables.table_schema:
CREATE TABLE attendance_records (
  student_id INT,
  session_id INT,
  attendance_status VARCHAR,
  timestamp TIMESTAMP
);

CREATE TABLE virtual_classroom_sessions (
    session_id UUID PRIMARY KEY,
    session_date DATE,
    session_duration INT
);

CREATE TABLE attendance_records (
    record_id SERIAL PRIMARY KEY,
    session_id UUID REFERENCES virtual_classroom_sessions(session_id),
    student_id UUID,
    attendance_status VARCHAR(10)
);

CREATE TABLE Student (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(100) NOT NULL
);

CREATE TABLE Classroom (
    classroom_id INT PRIMARY KEY,
    classroom_name VARCHAR(100) NOT NULL
);

CREATE TABLE Session (
    session_id INT PRIMARY KEY,
    session_date DATE NOT NULL,
    classroom_id INT REFERENCES Classroom(classroom_id)
);

CREATE TABLE Attendance (
    attendance_id INT PRIMARY KEY,
    session_id INT REFERENCES Session(session_id),
    student_id INT REFERENCES Student(student_id),
    attendance_status VARCHAR(10) NOT NULL
);

CREATE TABLE students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(50) NOT NULL
);

CREATE TABLE virtual_classrooms (
    classroom_id INT PRIMARY KEY,
    classroom_name VARCHAR(50) NOT NULL,
    session_date DATE NOT NULL
);

CREATE TABLE classroom_sessions (
    session_id INT PRIMARY KEY,
    classroom_id INT NOT NULL,
    session_date DATE NOT NULL
);

CREATE TABLE session_participants (
    participant_id INT PRIMARY KEY,
    session_id INT NOT NULL,
    student_id INT NOT NULL
);
