tables.table_schema:
CREATE TABLE materials (
  id SERIAL PRIMARY KEY,
  title VARCHAR(255),
  content TEXT
);

CREATE TABLE external_links (
  id SERIAL PRIMARY KEY,
  title VARCHAR(255),
  url VARCHAR(255)
);

CREATE TABLE reference_materials (
    id INT PRIMARY KEY,
    title TEXT,
    author TEXT,
    category TEXT
);

CREATE TABLE external_links (
    id INT PRIMARY KEY,
    title TEXT,
    url TEXT,
    description TEXT
);

CREATE TABLE materials_to_links (
    material_id INT REFERENCES reference_materials(id),
    link_id INT REFERENCES external_links(id)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    name TEXT,
    email TEXT
);

CREATE TABLE virtual_classroom_resource (
    resource_id INT PRIMARY KEY,
    resource_name TEXT,
    resource_type TEXT,
    resource_link TEXT
);

CREATE TABLE reference_material (
    material_id INT PRIMARY KEY,
    material_title TEXT,
    material_content TEXT,
    material_type TEXT
);

CREATE TABLE external_link (
    link_id INT PRIMARY KEY,
    link_title TEXT,
    link_url TEXT,
    link_description TEXT
);

CREATE TABLE resource_category (
    category_id INT PRIMARY KEY,
    category_name TEXT
);
