tables.table_schema:
CREATE TABLE StudentPoints ( 
   student_id INT PRIMARY KEY, 
   points INT, 
   badges INT 
);

CREATE TABLE points (
  student_id UUID,
  points INT,
  date_earned TIMESTAMP
);

CREATE TABLE badges (
  badge_id UUID,
  badge_name VARCHAR(50),
  student_id UUID,
  date_earned TIMESTAMP
);

CREATE TABLE points (
    student_id INT NOT NULL,
    points INT NOT NULL,
    date_earned DATE NOT NULL
);

CREATE TABLE students (
    student_id INT NOT NULL,
    student_name VARCHAR(50) NOT NULL,
    class_id INT NOT NULL,
    PRIMARY KEY (student_id)
);

CREATE TABLE points (
    student_id INT PRIMARY KEY,
    total_points INT
);

CREATE TABLE badges (
    badge_id INT PRIMARY KEY,
    badge_name VARCHAR,
    badge_description TEXT
);

CREATE TABLE student_badges (
    student_id INT,
    badge_id INT,
    FOREIGN KEY (student_id) REFERENCES points(student_id),
    FOREIGN KEY (badge_id) REFERENCES badges(badge_id)
);

CREATE TABLE student_progress (
    student_id INT,
    progress_percentage FLOAT,
    last_updated TIMESTAMP,
    FOREIGN KEY (student_id) REFERENCES points(student_id)
);

CREATE TABLE points (
    student_id INT PRIMARY KEY,
    total_points INT
);

CREATE TABLE badges (
    badge_id INT PRIMARY KEY,
    badge_name VARCHAR(50)
);

CREATE TABLE student_badges (
    student_id INT,
    badge_id INT,
    earned_date DATE,
    PRIMARY KEY (student_id, badge_id)
);

CREATE TABLE leaderboard (
    student_id INT PRIMARY KEY,
    rank INT
);

CREATE TABLE progress (
    student_id INT,
    level INT,
    progress_percentage FLOAT,
    PRIMARY KEY (student_id, level)
);
