CREATE TABLE students (
    student_id INT PRIMARY KEY,
    student_name TEXT,
    engagement_level INT,
    performance_level INT
);

CREATE TABLE activities (
    activity_id INT PRIMARY KEY,
    activity_name TEXT,
    activity_type TEXT,
    activity_points INT
);

CREATE TABLE students (
    student_id INT PRIMARY KEY,
    name VARCHAR,
    engagement_level INT,
    completed_activities INT
);

CREATE TABLE activities (
    activity_id INT PRIMARY KEY,
    activity_name VARCHAR,
    topic VARCHAR,
    difficulty_level INT
);

CREATE TABLE teachers (
    teacher_id INT PRIMARY KEY,
    name VARCHAR,
    subject VARCHAR
);

CREATE TABLE Teachers (
	teacher_id UUID PRIMARY KEY,
	teacher_name STRING NOT NULL,
	email STRING UNIQUE
);

CREATE TABLE Students (
	student_id UUID PRIMARY KEY,
	student_name STRING NOT NULL,
	student_age INT,
	student_level INT
);

CREATE TABLE Courses (
	course_id UUID PRIMARY KEY,
	course_name STRING NOT NULL,
	course_description STRING,
	course_teacher_id UUID REFERENCES Teachers
);

CREATE TABLE Assignments (
	assignment_id UUID PRIMARY KEY,
	assignment_name STRING NOT NULL,
	assignment_course_id UUID REFERENCES Courses,
	assignment_due_date TIMESTAMP
);

CREATE TABLE Grades (
	grade_id UUID PRIMARY KEY,
	grade_score FLOAT,
	grade_student_id UUID REFERENCES Students,
	grade_assignment_id UUID REFERENCES Assignments
);
