CREATE TABLE Students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(50) NOT NULL,
    progress_percentage DECIMAL(5,2) NOT NULL
);

CREATE TABLE Courses (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(50) NOT NULL,
    course_description TEXT
);

CREATE TABLE Enrollments (
    enrollment_id INT PRIMARY KEY,
    student_id INT REFERENCES Students(student_id),
    course_id INT REFERENCES Courses(course_id),
    enrollment_date DATE
);

CREATE TABLE students ( student_id SERIAL PRIMARY KEY,
  student_name VARCHAR(50) NOT NULL,
  progress FLOAT NOT NULL
);

CREATE TABLE courses ( course_id SERIAL PRIMARY KEY,
  course_name VARCHAR(50) NOT NULL,
  instructor VARCHAR(50) NOT NULL,
  semester VARCHAR(10) NOT NULL
);

CREATE TABLE assignments ( assignment_id SERIAL PRIMARY KEY,
  course_id INTEGER NOT NULL,
  deadline DATE NOT NULL,
  difficulty INTEGER NOT NULL
);

CREATE TABLE submissions ( submission_id SERIAL PRIMARY KEY,
  assignment_id INTEGER NOT NULL,
  student_id INTEGER NOT NULL,
  grade FLOAT NOT NULL
);

CREATE TABLE ar_activities ( activity_id SERIAL PRIMARY KEY,
  activity_name VARCHAR(50) NOT NULL,
  required_course INTEGER NOT NULL,
  description TEXT
);
