tables.table_schema:
CREATE TABLE ARContentMetadata (
    id SERIAL PRIMARY KEY,
    content_name TEXT NOT NULL,
    content_description TEXT,
    content_url TEXT NOT NULL
);

CREATE TABLE ARTags (
    tag_id SERIAL PRIMARY KEY,
    tag_name TEXT NOT NULL
);

CREATE TABLE ContentTagMapping (
    mapping_id SERIAL PRIMARY KEY,
    content_id INT REFERENCES ARContentMetadata(id),
    tag_id INT REFERENCES ARTags(tag_id)
);

CREATE TABLE ar_content_metadata (
    id SERIAL PRIMARY KEY,
    content_name VARCHAR(255) NOT NULL,
    author VARCHAR(100),
    creation_date DATE,
    hash VARCHAR(50) UNIQUE
);

CREATE TABLE ar_tags (
    tag_id SERIAL PRIMARY KEY,
    tag_name VARCHAR(50) UNIQUE
);

CREATE TABLE ar_content_tags (
    tag_id INT REFERENCES ar_tags(tag_id),
    content_id INT REFERENCES ar_content_metadata(id),
    PRIMARY KEY (tag_id, content_id)
);

CREATE TABLE ar_users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    email VARCHAR(100) UNIQUE
);

CREATE TABLE ar_tags (
    tag_id INT PRIMARY KEY,
    tag_name VARCHAR(50) UNIQUE
);

