tables.table_schema:
CREATE TABLE performance_data (
    student_id INT,
    session_id INT,
    performance_score INT,
    timestamp TIMESTAMP
);

CREATE TABLE StudentPerformance (
    student_id INT PRIMARY KEY,
    activity_id INT,
    performance_score FLOAT,
    date_recorded DATE
);

CREATE TABLE ActivityInformation (
    activity_id INT PRIMARY KEY,
    activity_name VARCHAR(255),
    activity_type VARCHAR(50),
    difficulty_level INT
);

CREATE TABLE performance_data (
  student_id INT,
  date DATE,
  assessment_name VARCHAR(50),
  score FLOAT
);

CREATE TABLE student_info (
  student_id INT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  date_of_birth DATE
);

CREATE TABLE assessment_results (
  student_id INT,
  assessment_name VARCHAR(50),
  date DATE,
  score FLOAT
);

CREATE TABLE performance_data (
    student_id INT,
    date DATE,
    performance_score FLOAT
);

CREATE TABLE student_details (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(50),
    birthdate DATE
);

CREATE TABLE educational_experiences (
    experience_id INT,
    experience_name VARCHAR(100),
    experience_type VARCHAR(50)
);

CREATE TABLE improvement_tracking (
    improvement_id INT,
    student_id INT,
    improvement_category VARCHAR(50),
    improvement_value FLOAT
);

CREATE TABLE students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(50) NOT NULL,
    age INT,
    grade_level VARCHAR(10)
);

CREATE TABLE courses (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(50) NOT NULL,
    instructor VARCHAR(50),
    year INT
);
