tables.table_schema:
CREATE TABLE UserFeedback (
   user_id INT PRIMARY KEY,
   feedback TEXT,
   timestamp TIMESTAMP
);

CREATE TABLE UserFeedback (
  user_id INT,
  activity_id INT,
  feedback_text VARCHAR(255),
  rating INT
);

CREATE TABLE UserFeedback (
  feedback_id SERIAL PRIMARY KEY,
  user_id INT,
  activity_id INT,
  feedback_text TEXT,
  rating INT
);

CREATE TABLE UserAssessment (
  assessment_id SERIAL PRIMARY KEY,
  user_id INT,
  activity_id INT,
  assessment_text TEXT,
  assessment_score INT
);

CREATE TABLE ActivityDetails (
  activity_id SERIAL PRIMARY KEY,
  activity_name VARCHAR(255),
  activity_description TEXT,
  activity_date DATE
);

CREATE TABLE UserActivityLog (
  log_id SERIAL PRIMARY KEY,
  user_id INT,
  activity_id INT,
  log_date TIMESTAMP
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255)
);

CREATE TABLE activities (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    description TEXT,
    category VARCHAR(50)
);

CREATE TABLE categories (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    description TEXT
);
