tables.table_schema:
CREATE TABLE learning_resources (
    resource_id INT PRIMARY KEY,
    resource_name VARCHAR(255),
    resource_type VARCHAR(50),
    subject VARCHAR(100)
);

CREATE TABLE student_preferences (
    student_id INT PRIMARY KEY,
    favorite_subject VARCHAR(100),
    preferred_resource_type VARCHAR(50),
    last_viewed_resource_id INT
);

CREATE TABLE resource_recommendations (
    recommendation_id INT PRIMARY KEY,
    student_id INT REFERENCES student_profiles(student_id),
    resource_id INT REFERENCES learning_resources(resource_id),
    recommendation_score FLOAT
);

CREATE TABLE categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50),
    category_description TEXT
);
