tables.table_schema:
CREATE TABLE user_goals (
    user_id INT PRIMARY KEY,
    learning_goal TEXT,
    assessment_progress FLOAT,
    last_activity TIMESTAMP
);

CREATE TABLE learning_goals (
    goal_id INT PRIMARY KEY,
    goal_name TEXT,
    description TEXT
);

CREATE TABLE quiz_questions (
    question_id INT PRIMARY KEY,
    question_text TEXT,
    correct_answer TEXT,
    learning_goal_id INT REFERENCES learning_goals(goal_id)
);

CREATE TABLE user_profiles (
    user_id INT PRIMARY KEY,
    username TEXT,
    email TEXT,
    learning_goal_id INT REFERENCES learning_goals(goal_id)
);

CREATE TABLE user_responses (
    response_id INT PRIMARY KEY,
    user_id INT REFERENCES user_profiles(user_id),
    question_id INT REFERENCES quiz_questions(question_id),
    user_answer TEXT
);

CREATE TABLE User (
    user_id INT PRIMARY KEY,
    full_name VARCHAR(255),
    email VARCHAR(255),
    learning_goal VARCHAR(255)
);

CREATE TABLE Quiz (
    quiz_id INT PRIMARY KEY,
    quiz_name VARCHAR(255),
    topic VARCHAR(255),
    difficulty INT
);

CREATE TABLE Question (
    question_id INT PRIMARY KEY,
    question_text VARCHAR(255),
    answer VARCHAR(255),
    quiz_id INT
);

CREATE TABLE UserQuizResult (
    result_id INT PRIMARY KEY,
    user_id INT,
    quiz_id INT,
    score INT
);

CREATE TABLE LearningGoal (
    goal_id INT PRIMARY KEY,
    goal_name VARCHAR(255),
    description TEXT
);
