tables.table_schema:
CREATE TABLE feedback_from_teachers (
    feedback_id INT PRIMARY KEY,
    teacher_id INT,
    feedback_text VARCHAR(255),
    feedback_date DATE
);

CREATE TABLE feedback_from_peers (
    feedback_id INT PRIMARY KEY,
    peer_id INT,
    feedback_text VARCHAR(255),
    feedback_date DATE
);

CREATE TABLE TeacherFeedback (
    teacher_id INT PRIMARY KEY,
    feedback_text TEXT,
    rating INT
);

CREATE TABLE PeerFeedback (
    peer_id INT PRIMARY KEY,
    feedback_text TEXT,
    rating INT
);

CREATE TABLE teachers (
  teacher_id INT PRIMARY KEY,
  teacher_name VARCHAR(50) NOT NULL,
  subject_taught VARCHAR(50) NOT NULL
);

CREATE TABLE peers (
  peer_id INT PRIMARY KEY,
  peer_name VARCHAR(50) NOT NULL,
  peer_role VARCHAR(50) NOT NULL
);
