tables.table_schema:
CREATE TABLE student_attendance (
    student_id INT PRIMARY KEY,
    date DATE,
    attended BOOLEAN
);

CREATE TABLE participation_data (
    student_id INT PRIMARY KEY,
    session_id INT,
    participation_score FLOAT
);

CREATE TABLE StudentAttendance (
    student_id INT,
    class_id INT,
    attendance_date DATE
);

CREATE TABLE ParticipationData (
    student_id INT,
    class_id INT,
    participation_date DATE,
    participation_type VARCHAR(50)
);

CREATE TABLE ClassSchedule (
    class_id INT,
    class_name VARCHAR(100),
    start_time TIME,
    end_time TIME
);

CREATE TABLE students (
  student_id INT PRIMARY KEY,
  first_name TEXT,
  last_name TEXT,
  email TEXT
);

CREATE TABLE courses (
  course_id INT PRIMARY KEY,
  course_name TEXT,
  instructor TEXT
);

CREATE TABLE attendance (
  attendance_id INT PRIMARY KEY,
  student_id INT,
  course_id INT,
  date DATE
);

CREATE TABLE participation (
  participation_id INT PRIMARY KEY,
  student_id INT,
  course_id INT,
  participation_grade FLOAT
);
