  learning_style VARCHAR(50),
  preferred_subjects VARCHAR(100),
  preferred_language VARCHAR(50)
);

CREATE TABLE preferences (
  student_id INT PRIMARY KEY,
  learning_style VARCHAR(255),
  interests TEXT
);

CREATE TABLE Students (
