tables.table_schema:
CREATE TABLE academic_fraud_detection (
  student_id INT PRIMARY KEY,
  submission_id INT,
  submission_date TIMESTAMP,
  submission_text TEXT
);

CREATE TABLE Student_Submissions (
    student_id INT,
    submission_id INT,
    submission_text TEXT
);

CREATE TABLE Plagiarism_Detections (
    detection_id INT,
    original_submission_id INT,
    detected_submission_id INT,
    plagiarism_score DECIMAL
);

CREATE TABLE Students (
  student_id INT PRIMARY KEY,
  student_name VARCHAR(50),
  submission_date DATE
);

CREATE TABLE Students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(50) NOT NULL
);

CREATE TABLE Assignments (
    assignment_id INT PRIMARY KEY,
    assignment_name VARCHAR(100) NOT NULL,
    assignment_due_date DATE
);

CREATE TABLE PlagiarismReports (
    report_id INT PRIMARY KEY,
    submission_id INT,
    original_submission_id INT,
    plagiarism_percentage DECIMAL(5,2)
);

CREATE TABLE SuspiciousActivity (
    activity_id INT PRIMARY KEY,
    student_id INT,
    activity_description TEXT,
    activity_date TIMESTAMP
);
