tables.table_schema:
CREATE TABLE CoursePreferences (
    course_id INT PRIMARY KEY,
    student_email VARCHAR(255),
    preference_rank INT
);

CREATE TABLE StudentAvailability (
    student_id INT PRIMARY KEY,
    day_of_week VARCHAR(10),
    start_time TIME,
    end_time TIME
);

CREATE TABLE Students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(50),
    student_grade INT
);

CREATE TABLE Courses (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(50),
    course_instructor VARCHAR(50)
);

CREATE TABLE Student (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(255),
    student_email VARCHAR(255),
    student_major VARCHAR(50)
);

CREATE TABLE StudentAvailability (
    student_id INT,
    available_day VARCHAR(10),
    available_time TIME,
    FOREIGN KEY (student_id) REFERENCES Student(student_id)
);

CREATE TABLE Course (
  course_id INT PRIMARY KEY,
  course_name VARCHAR(255) NOT NULL,
  course_time TIME NOT NULL
);

CREATE TABLE Student (
  student_id INT PRIMARY KEY,
  student_name VARCHAR(255) NOT NULL,
  student_email VARCHAR(255) UNIQUE
);

CREATE TABLE Preference (
  preference_id INT PRIMARY KEY,
  student_id INT REFERENCES Student(student_id),
  course_id INT REFERENCES Course(course_id),
  preference_score INT
);

CREATE TABLE Availability (
  availability_id INT PRIMARY KEY,
  student_id INT REFERENCES Student(student_id),
  day_of_week VARCHAR(15) NOT NULL,
  time_slot TIME NOT NULL
);
