tables.table_schema:
CREATE TABLE experiments (
  id INT PRIMARY KEY,
  experiment_name TEXT,
  experiment_date DATE
);

CREATE TABLE results (
  result_id INT PRIMARY KEY,
  experiment_id INT,
  result_data JSONB,
  FOREIGN KEY (experiment_id) REFERENCES experiments(id)
);

CREATE TABLE experiments (
    id INT PRIMARY KEY,
    experiment_name VARCHAR(50),
    researcher_name VARCHAR(50),
    experiment_date DATE
);

CREATE TABLE samples (
    sample_id INT PRIMARY KEY,
    experiment_id INT REFERENCES experiments(id),
    sample_type VARCHAR(50),
    sample_size FLOAT
);

CREATE TABLE results (
    result_id INT PRIMARY KEY,
    experiment_id INT REFERENCES experiments(id),
    sample_id INT REFERENCES samples(sample_id),
    result_value FLOAT
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50),
    research_field VARCHAR(50),
    contact_email VARCHAR(50)
);

CREATE TABLE researchers (
    id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    specialization VARCHAR(255),
    email VARCHAR(255)
);

CREATE TABLE samples (
    id INT PRIMARY KEY,
    sample_name VARCHAR(255),
    sample_type VARCHAR(255),
    experiment_id INT REFERENCES experiments(id)
);

CREATE TABLE publications (
    id INT PRIMARY KEY,
    publication_title VARCHAR(255),
    publication_date DATE,
    researcher_id INT REFERENCES researchers(id)
);
