tables.table_schema:
CREATE TABLE Educators (
    educator_id INT PRIMARY KEY,
    educator_name VARCHAR(50) NOT NULL,
    educator_email VARCHAR(100) UNIQUE NOT NULL,
    educator_department VARCHAR(50) NOT NULL
);

CREATE TABLE Researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50) NOT NULL,
    researcher_email VARCHAR(100) UNIQUE NOT NULL,
    researcher_field VARCHAR(50) NOT NULL
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    role VARCHAR(20) NOT NULL
);

CREATE TABLE Courses (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(100) NOT NULL,
    course_description TEXT,
    instructor_id INT NOT NULL REFERENCES Users(user_id)
);

CREATE TABLE ResearchPapers (
    paper_id INT PRIMARY KEY,
    paper_title VARCHAR(200) NOT NULL,
    author_id INT NOT NULL REFERENCES Users(user_id),
    publication_date DATE
);

CREATE TABLE Projects (
  project_id INT PRIMARY KEY,
  project_name VARCHAR(255),
  start_date DATE,
  end_date DATE
);

CREATE TABLE educators (
    educator_id INT PRIMARY KEY,
    educator_name VARCHAR(50),
    email VARCHAR(100),
    institution VARCHAR(100)
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50),
    email VARCHAR(100),
    research_area VARCHAR(100)
);

CREATE TABLE research_papers (
    paper_id INT PRIMARY KEY,
    paper_title VARCHAR(100),
    researcher_id INT,
    CONSTRAINT fk_researcher FOREIGN KEY (researcher_id) REFERENCES researchers(researcher_id)
);
