tables.table_schema:
CREATE TABLE Quiz (
    id INT PRIMARY KEY,
    student_id INT,
    quiz_name VARCHAR(50),
    score FLOAT
);

CREATE TABLE Test (
    id INT PRIMARY KEY,
    student_id INT,
    test_name VARCHAR(50),
    grade FLOAT
);

CREATE TABLE Quizzes (
    quiz_id INT PRIMARY KEY,
    student_id INT,
    quiz_date DATE,
    score FLOAT
);

CREATE TABLE Tests (
    test_id INT PRIMARY KEY,
    student_id INT,
    test_date DATE,
    score FLOAT
);

CREATE TABLE quizes (
    quiz_id INT PRIMARY KEY,
    quiz_name VARCHAR(100),
    course_id INT,
    created_date TIMESTAMP
);

CREATE TABLE questions (
    question_id INT PRIMARY KEY,
    question_text TEXT,
    quiz_id INT,
    correct_answer TEXT
);

CREATE TABLE students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(100),
    major VARCHAR(50),
    gpa FLOAT
);

CREATE TABLE QuizScores (
    student_id INT,
    quiz_id INT,
    score FLOAT
);

CREATE TABLE TestScores (
    student_id INT,
    test_id INT,
    subject VARCHAR(50),
    score FLOAT
);

CREATE TABLE Students (
    student_id INT,
    first_name VARCHAR(50),
    last_name VARCHAR(50),
    major VARCHAR(50)
);

CREATE TABLE Courses (
    course_id INT,
    course_name VARCHAR(100),
    instructor VARCHAR(50)
);

CREATE TABLE QuizQuestions (
    question_id INT,
    quiz_id INT,
    question_text TEXT
);
