tables.table_schema:
CREATE TABLE collaboration_opportunities (
    opportunity_id INT PRIMARY KEY,
    opportunity_description TEXT,
    posted_by VARCHAR(50) NOT NULL,
    posted_date DATE
);

CREATE TABLE experts (
    expert_id INT PRIMARY KEY,
    name VARCHAR,
    expertise_area VARCHAR,
    location VARCHAR
);

CREATE TABLE organizations (
    org_id INT PRIMARY KEY,
    org_name VARCHAR,
    industry VARCHAR
);

CREATE TABLE connections (
    connection_id INT PRIMARY KEY,
    expert_id INT,
    org_id INT,
    connection_date DATE
);

CREATE TABLE digital_literacy_experts (
  id INT PRIMARY KEY,
  name VARCHAR(100),
  expertise VARCHAR(200),
  organization VARCHAR(100)
);

CREATE TABLE collaborations (
  collaboration_id INT PRIMARY KEY,
  project_id INT,
  expert_id INT,
  collaboration_type VARCHAR(50)
);

CREATE TABLE skills (
  skill_id INT PRIMARY KEY,
  skill_name VARCHAR(50),
  description TEXT
);

CREATE TABLE meetings (
  meeting_id INT PRIMARY KEY,
  meeting_date DATE,
  location VARCHAR(100),
  agenda TEXT
);
