tables.table_schema:
CREATE TABLE teacher_availability (
 teacher_id SERIAL PRIMARY KEY,
 teacher_name VARCHAR(50) NOT NULL,
 teacher_email VARCHAR(100) NOT NULL,
 available_date DATE NOT NULL
);

CREATE TABLE parent_table (
    parent_id UUID PRIMARY KEY,
    parent_name VARCHAR(50) NOT NULL,
    parent_email VARCHAR(50) NOT NULL
);

CREATE TABLE teacher_table (
    teacher_id UUID PRIMARY KEY,
    teacher_name VARCHAR(50) NOT NULL,
    teacher_email VARCHAR(50) NOT NULL
);

CREATE TABLE conference_table (
    conference_id UUID PRIMARY KEY,
    parent_id UUID NOT NULL,
    teacher_id UUID NOT NULL,
    conference_date DATE NOT NULL
);

CREATE TABLE parent (
  parent_id INT PRIMARY KEY,
  parent_name VARCHAR(50) NOT NULL,
  email VARCHAR(50) NOT NULL
);

CREATE TABLE teacher (
  teacher_id INT PRIMARY KEY,
  teacher_name VARCHAR(50) NOT NULL,
  subject_taught VARCHAR(50) NOT NULL
);

CREATE TABLE parents (
    parent_id INT PRIMARY KEY,
    parent_name VARCHAR(50) NOT NULL,
    parent_email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE teachers (
    teacher_id INT PRIMARY KEY,
    teacher_name VARCHAR(50) NOT NULL,
    teacher_email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE conference_rooms (
    room_id INT PRIMARY KEY,
    room_name VARCHAR(50) NOT NULL,
    room_location VARCHAR(100) NOT NULL,
    max_capacity INT
);

CREATE TABLE agenda_items (
    agenda_id INT PRIMARY KEY,
    schedule_id INT REFERENCES conference_schedules(schedule_id),
    item_description TEXT NOT NULL
);
