tables.table_schema:
CREATE TABLE ParentVolunteerHours (
    parent_id INT PRIMARY KEY,
    parent_name VARCHAR(50) NOT NULL,
    volunteer_hours INT NOT NULL
);

CREATE TABLE parent_volunteer_hours (
    parent_id INT PRIMARY KEY,
    parent_name VARCHAR(50),
    hours_worked INT
);

CREATE TABLE school_recognition (
    school_id INT PRIMARY KEY,
    school_name VARCHAR(50),
    total_volunteer_hours INT
);

CREATE TABLE parent_volunteer_hours (
    parent_id INT PRIMARY KEY,
    volunteer_date DATE,
    hours_worked INT
);

CREATE TABLE parent_information (
    parent_id INT PRIMARY KEY,
    parent_name VARCHAR(50),
    email_address VARCHAR(100)
);

CREATE TABLE school_activities (
    activity_id INT PRIMARY KEY,
    activity_name VARCHAR(50),
    activity_date DATE
);

CREATE TABLE rewards (
    reward_id INT PRIMARY KEY,
    reward_name VARCHAR(50),
    reward_description TEXT
);
