tables.table_schema:
CREATE TABLE ParentalSurveyResponses (
  survey_id INT PRIMARY KEY,
  parent_id INT,
  response TEXT,
  timestamp TIMESTAMP
);

CREATE TABLE ParentalSurveyResponses (
  survey_id INT PRIMARY KEY,
  parent_id INT,
  question TEXT,
  response TEXT
);

CREATE TABLE ParentalEngagement (
  engagement_id INT PRIMARY KEY,
  parent_id INT,
  event_name TEXT,
  date DATE
);

CREATE TABLE ParentalFeedback (
  feedback_id INT PRIMARY KEY,
  parent_id INT,
  feedback_text TEXT,
  rating INT
);

CREATE TABLE ParentalSupport (
  support_id INT PRIMARY KEY,
  parent_id INT,
  support_type TEXT,
  support_date DATE
);

CREATE TABLE Students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(100),
    grade_level INT,
    parent_id INT
);

CREATE TABLE Teachers (
    teacher_id INT PRIMARY KEY,
    teacher_name VARCHAR(100),
    subject_taught VARCHAR(100)
);

CREATE TABLE Courses (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(100),
    teacher_id INT
);

CREATE TABLE ParentalInvolvement (
    id INT PRIMARY KEY,
    parent_id INT,
    involvement_type VARCHAR(100),
    involvement_level INT
);
