tables.table_schema:
CREATE TABLE DeliveryChannels (
    channel_id INT PRIMARY KEY,
    channel_name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE ParentalContactPreferences (
  parent_id INT PRIMARY KEY,
  email_contact BOOLEAN,
  sms_contact BOOLEAN,
  phone_contact BOOLEAN
);

CREATE TABLE EmailContacts (
  parent_id INT,
  email_address VARCHAR(255),
  PRIMARY KEY (parent_id, email_address),
  FOREIGN KEY (parent_id) REFERENCES ParentalContactPreferences(parent_id)
);

CREATE TABLE SMSContacts (
  parent_id INT,
  phone_number VARCHAR(15),
  PRIMARY KEY (parent_id, phone_number),
  FOREIGN KEY (parent_id) REFERENCES ParentalContactPreferences(parent_id)
);

CREATE TABLE PhoneContacts (
  parent_id INT,
  contact_number VARCHAR(15),
  PRIMARY KEY (parent_id, contact_number),
  FOREIGN KEY (parent_id) REFERENCES ParentalContactPreferences(parent_id)
);

CREATE TABLE EmailPreferences (
  id INT PRIMARY KEY,
  parent_id INT,
  email_updates BOOLEAN,
  promotional_emails BOOLEAN
);

CREATE TABLE SmsPreferences (
  id INT PRIMARY KEY,
  parent_id INT,
  sms_notifications BOOLEAN,
  marketing_messages BOOLEAN
);

CREATE TABLE PhoneCallPreferences (
  id INT PRIMARY KEY,
  parent_id INT,
  receive_calls BOOLEAN,
  marketing_calls BOOLEAN
);

CREATE TABLE NotificationHistory (
  id INT PRIMARY KEY,
  parent_id INT,
  notification_type VARCHAR(50),
  notification_timestamp TIMESTAMP
);
